import React, { useEffect, useState } from "react";
import "../App.css";

const Home = () => {
  const [news, setNew] = useState([]);
  const [loading, setLoading] = useState(true);

  const getnew = async () => {
    const res = await fetch(
      "https://newsapi.org/v2/everything?q=bitcoin&apiKey=c1f5ee1b3c6243e68cf3e091245df259"
    );
    const data = await res.json();
    setNew({ news: data.articles, loading: false });
    console.log(data);
  };
  useEffect(() => {
    getnew();
  }, []);

  // if (news == null) {
  //   return <div>Loading...</div>;
  // }
  return (
    <div className="row">
      <div className="col-3">sidebar</div>
      <div className="col-9">
        main content
        {news.map((val, key) => {
          return (
            <div>
              <h1>{val.author}</h1>
              <h1>{val.content}</h1>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Home;
