import React, { useEffect, useState } from "react";
import "../App.css";
import Navbar from "../Components/Navbar";

const Home = () => {
  const [news, setNew] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError]=useState

  const getnew = async () => {
    const res = await fetch(
      "https://newsapi.org/v2/everything?q=bitcoin&apiKey=c1f5ee1b3c6243e68cf3e091245df259"
    );
    const data = await res.json();
    setNew({ news: data.articles, loading: false });
    console.log(data);
  };
  useEffect(() => {
    getnew();
  }, [setNew]);

  // if (news == null) {
  //   return <div>Loading...</div>;
  // }
  return (
    <div className="row">
      <div className="col-3">sidebar</div>
      <div className="col-9 bg-warning">
        main content
        {news?.map((val, key) => {
          const [author, content, description, publishedAt, title] = val;
          return (
            <div
              key={key.id}
              style={{
                backgroundColor: "red",
                width: "300px",
                height: "400px",
              }}
            >
              <h1>{author}</h1>
              <h1>{content}</h1>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Home;
